INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Man kann ihn in den Minen der Dunklen Mäander finden;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Sie wirkte unermesslich groß und unerträglich schön;
