INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Sie wirkte unermesslich groß und unerträglich schön;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('„Hoffnung entfachte er neu, und in Hoffnung endete er;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('« L''espoir il a ravivé, et dans l''espoir il a fini;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"La speranza riaccese e nella speranza se ne andò;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"A esperança ele reacendeu, e em esperança ele se foi;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Der eigene, kleine Garten eines freien Gärtners war alles, was Sam verlangte und ihm zustand, nicht ein Garten-Reich;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Lo único que respondía a los gustos y necesidades de Sam era un pequeño jardín de un jardinero libre, y no un jardín de las dimensiones de un reino;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Il piccolo giardino di un giardiniere libero era tutto ciò di cui Sam aveva bisogno e non di un giardino ingrandito come un reame;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Um jardim pequeno de um jardineiro livre era tudo a que Sam tinha direito e de que precisava, não um jardim inchado até virar um reino;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('La tavola fu apparecchiata in un batter d''occhio. C''erano zuppa calda, carne fredda, una crostata di more, pagnotte di pane fresco, panetti di burro e una mezza forma di formaggio invecchiato;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Il Grande Anello d''ora in poi apparterrà al Regno del Nord;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"O Anel Maior passará a ser uma herança do Reino do Norte;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Ils regardèrent à l''est vers l''Ombre et à l''ouest vers le Crépuscule;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Guardarono a oriente l''Ombra e a occidente il Crepuscolo;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Frodo war kaum weniger verängstigt als seine Begleiter;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Frodo non era meno spaventato dei suoi compagni: tremava come per un gran freddo;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Frodo estava quase tão aterrorizado quanto seus companheiros;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Il mio sguardo si è posato su Moria;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Canté sobre hojas, sobre hojas de oro, y allí crecieron hojas de oro;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('« Les trois Anneaux des Elfes ne furent pas créés comme des armes de guerre ou de conquête;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"I Tre Anelli degli Elfi non furono fabbricati per servire come armi di guerra e di conquista: non è questo il loro potere. Coloro che li forgiarono non desideravano forza o dominazione, né accumulavano tesori;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"En Dwimordene, en Lórien;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Canta! Perché il bagno sul finir del giorno\nSai che laverà via il fango più immondo!\nPazzo è colui che si rifiuta di cantare;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('« Les anneaux magiques sont... magiques;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Gli anelli magici sono, come dire... magici;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Anéis mágicos são... bem... mágicos;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"I *palantíri* furono fatti dai Noldor;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Faramir und Éowyn traten vor und legten Hand in Hand;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Faramir et Éowyn se levèrent et se tinrent main dans la main;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Faramir ed Éowyn si fecero avanti e si presero per mano;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Faramir e Éowyn se adiantaram e deram as mãos;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Nicht alles, was glänzt, ist Gold,\nnicht alle, die wandern, verirrt;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('No todo lo que es oro reluce\nni todos los errantes están perdidos.\nEl viejo fuerte no se desluce;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Tout ce qui est or ne brille pas,\nNe sont pas perdus tous ceux qui errent;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Non tutto quel che è oro brilla,\nNé gli erranti si smarriscono;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Nem tudo que é ouro rebrilha,\nNem tudo que vaga se perde;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Das Auenland hatte selten einen so schönen Sommer oder einen so reichhaltigen Herbst erlebt;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('La Comté avait rarement connu un été si radieux ou un automne si riche;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Raramente o Condado vira verão tão belo, nem outono tão rico;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Unter den Hobbits aus dem Westviertel hieß es, von der Spitze des Turms könne man das Meer sehen;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Os Hobbits da Quarta Oeste diziam que dava para ver o Mar do topo daquela torre;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('„Nun hängt eine große Wolke über allem Land von hier bis zu den Schattenbergen;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Ahora la nube se cierne sobre toda la región, entre aquí y las Montañas de la Sombra;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Agora a grande nuvem paira sobre toda a terra daqui até as Montanhas da Sombra;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('„Die Welt ist in der Tat voller Gefahren, und es gibt viele finstere Orte;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('« Le monde est en effet plein de périls et on y trouve bien des endroits sombres;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Il mondo è davvero pieno di pericoli e vi sono molti posti oscuri;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"O mundo está mesmo cheio de perigos, e nele há muitos lugares sombrios;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Cuando el invierno comienza a morder\ny las piedras crujen en la noche helada;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('„Die ältesten Ebereschen wurden von den Ents gepflanzt, um die Entfrauen zu erfreuen;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Los fresnos más antiguos los plantaron los ents para complacer a las ent-mujeres;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('« Les plus anciens sorbiers avaient été plantés par les Ents pour tenter de plaire aux Ents-femmes;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"As tramazeiras mais antigas foram plantadas pelos Ents para tentar agradar as Entesposas;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('„Solange das Auenland sicher ist, finde ich das Wandern erträglicher;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Fin quando la Contea è al sicuro, girovagare sarà per me più facile;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Enquanto o Condado permanecer seguro, eu acharei mais suportável vagar;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Legolas baute in Ithilien ein graues Schiff, segelte damit den Anduin hinab und weiter, übers Meer;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Legolas costruì nell''Ithilien una barca grigia, discese il corso dell''Anduin e traversò il Mare;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Legolas construiu um barco cinza em Ithilien e velejou pelo Anduin e além Mar;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Me ne vado;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Estou partindo;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Fui longe para achar essas folhas;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Schließlich erreichten sie die Wetterspitze;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Y llegaron por fin a la Cima de los Vientos;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Giunsero finalmente a Colle Vento;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Pouco depois eles chegaram ao Topo do Vento;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Las flores brillaban en tonos dorados y rojizos;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('A sud il reame di Gondor durò a lungo;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Ma stagni e ruscelli sono freschi, sono belli;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Língua de Cobra mostrou os dentes;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Que se te enfríen las manos, los huesos y el pecho,\nque el sueño bajo la piedra sea tu tumba;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Los muertos despiertan;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('« Les morts se réveillent;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Os mortos despertam;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Había muchos más orcos de lo que habíamos creído;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('„Wenn du versuchst, zu fliehen, müssen wir dich fesseln;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Se cercherai di scappare, dovremo legarti;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Se você tentar fugir vai ser preciso amarrá-lo;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('„Sie setzten mich auf der Spitze des Orthanc aus. Ich stand dort allein auf einer Insel in den Wolken und konnte auf keine Rettung hoffen;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Eles me pegaram e me deixaram no pináculo de Orthanc. Fiquei sozinho numa ilha entre as nuvens;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('„Für mich ist es eine traurige Geschichte;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('„Sie werden ihn stehlen;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('„Es war aus Mitleid, dass Bilbos Hand zurückhielt. Wünsche ihm nicht allzu eifrig den Tod;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"La lástima fue lo que detuvo la mano de Bilbo. No te apresures a dispensar la muerte;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Foi a piedade que conteve a mão de Bilbo. Não se apresse em julgar e condenar à morte;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Isildur schnitt mit dem Heft des zersplitterten Schwertes seines Vaters den Ring von Saurons Hand;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('„Und das war''s. Ein böses Ende für Saruman und ich wünschte, ich hätte es nicht mitansehen müssen;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('« Et c''en est fini. C''est une sale fin pour Saruman, et j''aurais préféré ne pas avoir eu besoin d''y assister;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"E esse é o fim. Um fim terrível para Saruman, e eu não queria ter precisado ver isso;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('„Ihr hofft auf den Sonnenaufgang? Wir sind die Uruk-hai;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"¿Qué tiene que ver el alba? Somos los uruk-hai;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('« Quoi, l''aube ? Nous sommes les Uruk-hai;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Che importa l''alba? Siamo gli Uruk-hai, la nostra lotta non si ferma mai;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('"Aurora o quê? Nós somos os Uruk-hai;
INSERT INTO cardForeignData (flavorText, language, multiverseId, name, text, type, uuid) VALUES ('Es ertönte grobes Gelächter, wie das Rasseln von Steinen, die in eine Grube fallen;
